mapper.select:
SELECT WRITINGGROUP_NAME, TITLE, NAME , DATEOFPREPARATION FROM ((COMMUNITY_LIST INNER JOIN WRITING_GROUP ON COMMUNITY_LIST.WRITINGGROUP_ID = WRITING_GROUP.WRITINGGROUP_ID) INNER JOIN MEMBER ON COMMUNITY_LIST.MEMBER_ID = MEMBER.MEMBER_ID) ORDER BY DATEOFPREPARATION DESC;
and TITLE like CONCAT(#{words}, '%')
and NAME like CONCAT(#{words}, '%')
limit #{pageBegin}, #{pageScale} ;
